    <script>
        function clearLocalStorage() {
            console.error('Xoá local storage')
            localStorage.clear();
        }

        $(document).ready(function () {
            let url = window.location;

            let element = $('a').filter(function () {
                return this.href === url || url
                    .href
                    .indexOf(this.href) === 0;
            });
            if (element) {
                var path = window.location.pathname;
                for (let i = 0; i < element.length; i++) {
                    let h = element[i].getAttribute('href') ? element[i].getAttribute('href').split("?")[0] :
                        null
                    if (path == h) {
                        element[i].classList.add("active-dts")
                        let c = element[i].parentNode;
                    }
                }
            }
        });
        if ($('#back-to-top').length) {
            // console.log('check back to top');
            var scrollTrigger = 200, // px
                backToTop = function () {
                    var scrollTop = $(window).scrollTop();
                    if (scrollTop > scrollTrigger) {
                        $('#back-to-top').addClass('show');
                    } else {
                        $('#back-to-top').removeClass('show');
                    }
                };
            backToTop();
            $(window).on('scroll', function () {
                backToTop();
            });
            $('#back-to-top').on('click', function (e) {
                e.preventDefault();
                $('html,body').animate({
                    scrollTop: 0
                }, 700);
            });
        }

    </script>
